import axios from "axios";

const API_URL = "http://localhost:5132/api";

export const fetchProducts = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/products`);
    return response.data;
  } catch (error) {
    console.error("Error fetching products:", error);
    throw error;
  }
};

export const addToCart = async (productId, quantity) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/cart`, { productId, quantity });
    return response.data;
  } catch (error) {
    console.error("Error adding to cart:", error);
    throw error;
  }
};

export const updateCartQuantity = async (cartItemId, quantity) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/cart/update-quantity/${cartItemId}`, { quantity });
    return response.data;
  } catch (error) {
    console.error("Error updating cart quantity:", error);
    throw error;
  }
};

export const removeFromCart = async (cartItemId) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/cart/${cartItemId}`);
    return response.data;
  } catch (error) {
    console.error("Error removing from cart:", error);
    throw error;
  }
};
export const placeOrder = async () => {
  try {
    const response = await axios.post(`${API_BASE_URL}/orders`);
    return response.data;
  } catch (error) {
    console.error("Error placing order:", error);
    throw error;
  }
};
