import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProductsRequest, addToCart } from '../redux/actions';
import "../styles.css"; 
import thImage from '../Images/th.jpg'; 

const ProductList = () => {
  const dispatch = useDispatch();
  const products = useSelector(state => state.product.products);
  const loading = useSelector(state => state.product.loading);
  const error = useSelector(state => state.product.error);

  // Example mock data (for testing)
  const mockProducts = [
    {
      id: 1,
      name: "Product 1",
      price: 20,
      availableQuantity: 10,
      imageUrl: "http://example.com/product1.jpg",
    },
    {
      id: 2,
      name: "Product 2",
      price: 30,
      availableQuantity: 5,
      imageUrl: "http://example.com/product2.jpg",
    },
  ];

  // Replace with mock data if no products from Redux
  const displayProducts = products.length > 0 ? products : mockProducts;

  useEffect(() => {
    dispatch(fetchProductsRequest());
  }, [dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  if (loading) return <p className="loading">Loading products...</p>;

  if (error) return <p className="error">Error: {error.message}</p>;

  return (
    <div className="product-list">
      <h1>Our Products</h1>
      <div className="product-grid">
        {displayProducts && displayProducts.length > 0 ? (
          displayProducts.map(product => (
            <div key={product.id} className="product-item">
              <img 
                src={product.imageUrl || thImage} 
                alt={product.name} 
                className="product-image" 
              />
              <h2>{product.name}</h2>
              <p className="price">Price: ${product.price}</p>
              <p>Available: {product.availableQuantity}</p>
              <button 
                onClick={() => handleAddToCart(product)} 
                disabled={product.availableQuantity === 0}
              >
                {product.availableQuantity === 0 ? 'Out of Stock' : 'Add to Cart'}
              </button>
            </div>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </div>
    </div>
  );
};

export default ProductList;
