// actions.js
export const fetchProductsRequest = () => {
  return (dispatch) => {
    const mockProducts = [
      { id: 1, name: "Product 1", price: 20, availableQuantity: 10, imageUrl: "http://example.com/product1.jpg" },
      { id: 2, name: "Product 2", price: 30, availableQuantity: 5, imageUrl: "http://example.com/product2.jpg" },
    ];

    dispatch({
      type: 'FETCH_PRODUCTS_SUCCESS',
      payload: mockProducts,
    });
  };
};

export const addToCart = (product) => {
  return {
    type: 'ADD_TO_CART',
    payload: product,
  };
};
